import React from 'react';
import Navbar from '../components/Navbar';
import HomePage from './HomePage';
import { useState, useEffect } from 'react'

const UserPageWrapper = (props) => {

    const [userData, setUserData] = useState(null);         // JSON     {}
    const [userRepos, setUserRepos] = useState(null);       // Array    []

    useEffect(() => {
        const fetchData = async () => {
            try {
              const userData = await getUserData();
              if (userData.login) {
                await getRepoData(userData.login)
        
              }
            } catch (error) {
              console.error("Error fetching data:", error);
            }
          };

        fetchData()
    })

    const getUserData = async () => {
        const response = await fetch("http://localhost:4000/getUserDataSearch", {
          method: "GET",
          headers: { "Authorization": "Bearer " + localStorage.getItem("accessToken") }
        });
    
        if (!response.ok) 
          throw new Error('Failed to fetch user data');
    
        const data = await response.json();
        setUserData(data);
        return data;
    }

    const getRepoData = async (username) => {
        const response = await fetch(`http://localhost:4000/getRepoData?username=${username}`, {
          method: "GET",
          headers: { "Authorization": "Bearer " + localStorage.getItem("accessToken") }
        });
    
        if (!response.ok) throw new Error('Failed to fetch repository data');
        const data = await response.json();
        setUserRepos(data);
    }
    
    
    const handleSearch = async (username) => {
        try {
            const userResponse = await fetch(`https://api.github.com/users/${username}`);
            const userData = await userResponse.json();
            setUserData(userData);
            console.log(userData)

            const reposResponse = await fetch((`https://api.github.com/users/${username}/repos`), {
                method: "GET",
                headers: { "Authorization": "Bearer " + localStorage.getItem("accessToken") }
              });
          
              if (!response.ok) throw new Error('Failed to fetch repository data');
              const data = await response.json();
              setUserRepos(data);
          }
        catch (error) {
            console.error("Failed to fetch user data:", error);
        }
    };

    return (
        <div>
            <Navbar onSearch={ handleSearch }/>
            <HomePage propUserData={ userData } propUserRepos={ userRepos } />
        </div>
    );
    };

export default UserPageWrapper;
