import React, { useState, useEffect } from 'react';
import '../styling/quick-facts.css';

const QuickFacts = ({ userRepos }) => {
    const [totalCommits, setTotalCommits] = useState(0);

    useEffect(() => {
        if (userRepos.length > 0) {
            const accessToken = localStorage.getItem("accessToken");
            const commitsUrls = userRepos.map(repo => repo.commits_url.replace('{/sha}', ''));

            getTotalCommits(commitsUrls, accessToken)
                .then(setTotalCommits)
                .catch(error => {
                    console.error('Error fetching commits:', error);
                });
        }
    }, [userRepos]);

    async function fetchCommits(url, accessToken) {
        const response = await fetch(url, {
            headers: {
                "Authorization": `Bearer ${accessToken}`,
                "Accept": "application/vnd.github.v3+json"
            }
        });
        const commits = await response.json();
        return commits.length;
    }

    async function getTotalCommits(commitsUrls, accessToken) {
        let total = 0;
        for (const url of commitsUrls) {
            total += await fetchCommits(url, accessToken);
        }
        return total;
    }

    return (
        <div className='facts-container'>
            <div className='fact-box'>
                <h1>
                    {totalCommits}
                </h1>
                <p>commits</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>lines</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>languages</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>rank</p>
            </div>
        </div>
    )
}

export default QuickFacts
