import React from 'react'
import '../styling/quick-facts.css'
import { useState } from 'react'

const QuickFacts = ({ userRepos }) => {

    const numOfCommits = () => {
        const total = userRepos.reduce((accumulator, currentRepo) => {
          return accumulator + currentRepo.size;
        }, 0);
    
        return total;
      };

    return (
        <div className='facts-container'>
            <div className='fact-box'>
                <h1>
                    {userRepos.size}
                </h1>
                <p>commits</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>lines</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>languages</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>rank</p>
            </div>
        </div>
    )
}

export default QuickFacts
