import React from 'react'
import '../styling/quick-facts.css'
import { useState, useEffect } from 'react'

const QuickFacts = ({ userRepos }) => {

    const [totalCommits, setTotalCommits] = useState(0);

    useEffect(() => {
        if (userRepos.length > 0) {
            const accessToken = localStorage.getItem("accessToken"); // Retrieve access token from localStorage
            getTotalCommits(userRepos, accessToken).then(setTotalCommits);
        }
    }, [userRepos]); // Depend on userRepos

    const commitsUrls = reposData.map(repo => {
        return repo.commits_url.replace('{/sha}', ''); // Remove the '{/sha}' part
      });
      
    async function fetchCommits(url, accessToken) {
        const response = await fetch(url, {
          headers: {
            "Authorization": `Bearer ${accessToken}`, // Assuming you have an access token
            "Accept": "application/vnd.github.v3+json"
          }
        });
        const commits = await response.json();
        return commits.length; // Return the number of commits
      }
      
    async function getTotalCommits(commitsUrls, accessToken) {
        let totalCommits = 0;
      
        for (const url of commitsUrls) {
          const commitCount = await fetchCommits(url, accessToken);
          totalCommits += commitCount;
        }
      
        return totalCommits;
    }

    getTotalCommits(commitsUrls, accessToken)
        .then(totalCommits => {
            console.log('Total commits across all repositories:', totalCommits);
        })
        .catch(error => {
            console.error('Error fetching commits:', error);
        });

    return (
        <div className='facts-container'>
            <div className='fact-box'>
                <h1>
                    
                </h1>
                <p>commits</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>lines</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>languages</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>rank</p>
            </div>
        </div>
    )
}

export default QuickFacts
