            console.error("Failed to fetch user data:", error);
        }
    };
    return (
        <div>
            <Navbar onSearch={ handleSearch }/>
            <HomePage propUserData={ userData } propUserRepos={ userRepos } />
        </div>
    );
    };

export default UserPageWrapper;
