import React from 'react'
import '../styling/quick-facts.css'
import { useState, useEffect } from 'react'

const QuickFacts = ({ userRepos }) => {

    const [totalCommits, setTotalCommits] = useState(0);

    useEffect(() => {
        if (userRepos.length > 0) {
            const accessToken = localStorage.getItem("accessToken"); // Retrieve access token from localStorage
            getTotalCommits(userRepos, accessToken).then(setTotalCommits);
        }
    }, [userRepos]); // Depend on userRepos

    getTotalCommits(commitsUrls, accessToken)
    .then(totalCommits => {
        console.log('Total commits across all repositories:', totalCommits);
    })
    .catch(error => {
        console.error('Error fetching commits:', error);
    });

    return (
        <div className='facts-container'>
            <div className='fact-box'>
                <h1>
                    
                </h1>
                <p>commits</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>lines</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>languages</p>
            </div>
            <div className='fact-box'>
                <h1>
                    #
                </h1>
                <p>rank</p>
            </div>
        </div>
    )
}

export default QuickFacts
